import React, { useState } from 'react';  // Keep this import statement

import './customer.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';





export default function customer() {

  const handleInputChange = (event) => {
    const { name, value } = event.target;

    setState(prevState => ({
        ...prevState,
        [name]: value
    }));
};


const [id,setId]= useState('');
const [name,setName]=useState('');
const [address,setAddress]=useState('');
const [age,setAge]=useState('');


const handleButtonClick = () => {
  const formData = {
    id: id,
    name: name,
    address: address,
    age: age,
  };

  if (!id || !name || !address || !age) {
    alert("Please fill in all fields before saving.");
  } else {
    axios.post('http://localhost:8000/save/pos', formData)
      .then((response) => {
        alert("Save successfully!");
      
        // Call getCustomers after successful save
       
      })
      .catch((error) => {
        console.error('Error saving data:', error);
      });
  }
};



  return (
    <div>
      <h1 className='h1'>Customer Form</h1>

    <input placeholder='Customer Id'value={id} className='idText' type="text" name="id" onChange={handleInputChange}  />

   
   <input placeholder='Customer Name'value={name} className='idText' type="text" name="name"onChange={handleInputChange}/>


   <input placeholder='Customer Address'value={address} className='idText' type="text" name="address" onChange={handleInputChange} />


    <input placeholder='Customer Age'value={age} className='idText' type="text" name="age" onChange={handleInputChange} />

    <div className="btnDiv">
       
       <button id='saveBtn' className="btn btn-primary" onClick={handleButtonClick} >
         Save
       </button>
    
       <button id='updateBtn' className="btn btn-warning"  >
         Update
       </button>
     
       <button id='deletBtn' className="btn btn-danger" >
         Delete
       </button>


    
   </div>

   <div className="table-container">
             
             <table className="table scaledTable">
               <thead>
                 <tr>
                   <th style={{ position: 'sticky', top: '0', backgroundColor: 'white' }}>Customer Id</th>
                   <th style={{ position: 'sticky', top: '0', backgroundColor: 'white' }}>Customer Name</th>
                   <th style={{ position: 'sticky', top: '0', backgroundColor: 'white' }}>Customer Address</th>
                   <th style={{ position: 'sticky', top: '0', backgroundColor: 'white' }}>Customer Age</th>
                 </tr>
               </thead>
               <tbody className='tBody' >
                
                   <tr >
                     <td>C00-001</td>
                     <td>Maduranga</td>
                     <td>Galle</td>
                     <td>078903756</td>
                   </tr>
                
               </tbody>
             </table>
               </div>

  </div>
  


  )
}
