import React, { useState } from 'react';
import './customer.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

export default function Customer() {
  const [formData, setFormData] = useState({
    id: '',
    name: '',
    address: '',
    age: ''
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleButtonClick = () => {
    const { id, name, address, age } = formData;
    const formDataToSend = { id, name, address, age };

    if (!id || !name || !address || !age) {
      alert("Please fill in all fields before saving.");
    } else {
      axios.post('http://localhost:8000/save/pos', formDataToSend)
        .then((response) => {
          alert("Save successfully!");
          setFormData({
            id: '',
            name: '',
            address: '',
            age: ''
          });
          // Call getCustomers after successful save
          // getCustomers();
        })
        .catch((error) => {
          console.error('Error saving data:', error);
        });
    }
  };

  return (
    <div>
      <h1 className='h1'>Customer Form</h1>
      <input placeholder='Customer Id' value={formData.id} className='idText' type="text" name="id" onChange={handleInputChange} />
      <input placeholder='Customer Name' value={formData.name} className='idText' type="text" name="name" onChange={handleInputChange} />
      <input placeholder='Customer Address' value={formData.address} className='idText' type="text" name="address" onChange={handleInputChange} />
      <input placeholder='Customer Age' value={formData.age} className='idText' type="text" name="age" onChange={handleInputChange} />
      <div className="btnDiv">
        <button id='saveBtn' className="btn btn-primary" onClick={handleButtonClick}>
          Save
        </button>
        <button id='updateBtn' className="btn btn-warning">
          Update
        </button>
        <button id='deletBtn' className="btn btn-danger">
          Delete
        </button>
      </div>
      <div className="table-container">
        <table className="table scaledTable">
          <thead>
            <tr>
              <th style={{ position: 'sticky', top: '0', backgroundColor: 'white' }}>Customer Id</th>
              <th style={{ position: 'sticky', top: '0', backgroundColor: 'white' }}>Customer Name</th>
              <th style={{ position: 'sticky', top: '0', backgroundColor: 'white' }}>Customer Address</th>
              <th style={{ position: 'sticky', top: '0', backgroundColor: 'white' }}>Customer Age</th>
            </tr>
          </thead>
          <tbody className='tBody' >
            <tr >
              <td>C00-001</td>
              <td>Maduranga</td>
              <td>Galle</td>
              <td>078903756</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
}
