import React, { useState } from 'react';
import './singUp.css'
import { useNavigate } from 'react-router-dom';

export default function SignUp() {

    const navigate = useNavigate();


    const[firstname ,setFirstName]=useState('');
    const[lastname ,setLastname]=useState('');
    const[address ,setAddress]=useState('');
    const[username ,setUsername]=useState('');
    const[password,setPassword]=useState('');
    const[conformPassword ,setConformPassword]=useState('');

    const handleInputChange = (event) => {
        const { name, value } = event.target;
    
        switch (name) {
          case 'firstname':
            setFirstName(value);
            break;
          case 'lastname':
            setLastname(value);
            break;
          case 'address':
            setAddress(value);
            break;
          case 'username':
            setUsername(value);
            break;
         case 'password':
            setPassword(value);
            break;
         case 'conformPassword':
            setConformPassword(value);
            break;
          default:
            break;
        }
      };


  const loginClick=()=>{

    navigate('/'); 

  }

  return (
    <div className="signup-container">
      <h2>Sign Up</h2>
      <form>
        <input type="text" name="firstname" placeholder="First Name" required onChange={handleInputChange}/>
        <input type="text" name="lastname" placeholder="Last Name" required onChange={handleInputChange} />
        <input type="text" name="address" placeholder="Address" required onChange={handleInputChange} />
        <input type="text" name="username" placeholder="Username" required  onChange={handleInputChange}/>
        <input type="password" name="password" placeholder="Password" required onChange={handleInputChange}/>
        <input type="password" name="confirmpassword" placeholder="Confirm Password" required  onChange={handleInputChange}/>
        <input type="submit" value="Sign Up" />

        <h4 type="submit" onClick={loginClick} > Login</h4>

      </form>
    </div>
  );
}
